SELECT * FROM ( SELECT ROWNUM RNUM, A.* FROM (SELECT
USER_ID ,REPLY_NO ,REPORT_CONTENT ,RECIPE_TITLE ,REPLY_CONTENT ,R.ENROLL_DATE ,REPORT_STATUS
FROM REPLY R JOIN MEMBER USING(USER_NO) JOIN REPORT USING(USER_NO) JOIN RECIPE USING(RECIPE_NO) WHERE REPORT_STATUS = 'Y' AND DELETE_STATUS = 'N' ORDER BY ENROLL_DATE DESC
)A
)
WHERE RNUM BETWEEN ? AND ?
